// floor of logBase
function integer ~INCLUDENAME[0];
  input integer base, value;
  begin
    for (~INCLUDENAME[0] = 0; value >= base; ~INCLUDENAME[0]=~INCLUDENAME[0]+1)
      value = value / base;
  end
endfunction

// ceiling of logBase
function integer ~INCLUDENAME[0];
  input integer base, value;
  begin
    for (~INCLUDENAME[0] = 0; base ** ~INCLUDENAME[0] < value; ~~INCLUDENAME[0]=~INCLUDENAME[0]+1);
  end
endfunction

