BlackBox.template:
// replaceBit start
always_comb begin
  ~RESULT = ~ARG[1];
  ~RESULT[~ARG[2]] = ~ARG[3];
end
// replaceBit end

// setSlice begin
always_comb begin
  ~RESULT = ~ARG[0];
  ~RESULT[~LIT[1] : ~LIT[2]] = ~ARG[3];
end
// setSlice end

// rotateL begin
logic [2*~SIZE[~TYPO]-1:0] ~GENSYM[bv][0];
assign ~SYM[0] = {~ARG[1],~ARG[1]} << (~ARG[2] % ~SIZE[~TYPO]);
assign ~RESULT = ~SYM[0][2*~SIZE[~TYPO]-1 : ~SIZE[~TYPO]];
// rotateL end

// rotateR begin
logic [2*~SIZE[~TYPO]-1:0] ~GENSYM[bv][0];
assign ~SYM[0] = {~ARG[1],~ARG[1]} >> (~ARG[2] % ~SIZE[~TYPO]);
assign ~RESULT = ~SYM[0][~SIZE[~TYPO]-1 : 0];
// rotateR end
